import { Button } from "@mui/material"
import { useEffect, useState } from "react"


function Pagination({showPerPage}){
    // console.log(showPerPage);
    const [counter,setCounter]=useState(1);

    useEffect(()=>{
        const value= showPerPage*counter;
        console.log("startvalue:",value)
    })




    return(
        <>
        <Button style={{textAlign:'left'}} variant="contained"
        onClick={()=>setCounter(counter-1)}>Previous</Button>
        <Button style={{textAlign:'right'}} variant="contained"
        onClick={()=>setCounter(counter+1)}>Next</Button>
        </>
    )
}
export default Pagination
